import React from 'react';
import ReactDOM from 'react-dom';
const alertOnion = (event) => {
    if (event.target.value.includes('cebolla')) {
        alert('¡Odio la Cebolla!')
    }
    else {
        console.log('Puedo soportarlo')
    }
}



class OnionHater extends React.Component {
    render() {
        const alertText = (<textarea onChange={alertOnion}>Escribe aquí</textarea>);

        return (
            <textarea className="textarea" rows="10" cols="30" />
        )
    }
}

export default OnionHater;
ReactDOM.render(alertText, document.getElementById('root'));
