import React from 'react';



class OnionHater extends React.Component {
    constructor(props) {
        super(props);
        this.isHating = false
        this.handleChange = this.handleChange.bind(this);
    }


    handleChange(event) {
        const cebolla = event.target.value
        if (cebolla.includes('cebolla')) {
            this.isHating = true;
            this.forceUpdate();
        }
        else {
            this.isHating = false
            this.forceUpdate();
        }
    }
    changeColor() {
        if (this.isHating === true) {
            return
            event.target.classList.add('red');
        } else {
            event.target.classList.remove('red');
        }
    }
    render() {

        return (
            <textarea onChange={this.handleChange} className="textarea" rows="10" cols="30" />
        )
    }
}

export default OnionHater;
